import './MoviesCard.css';
import { CurrentUserContext } from "../../contexts/CurrentUserContext";

export default function MoviesCard(movie) {
  const currentUser = React.useContext(CurrentUserContext);
  const isOwn = movie.owner === currentUser._id;
  return (
    <div className='movie'>
      {isOwn && <button className='element__delete element__delete_active' onClick={handleDeleteClick} />}
      <img alt={movie.name} className="element__image" src={movie.link} onClick={handleCardClick} />
