import React from 'react';
import './Login.css';
import { useState } from 'react';
import { Link } from "react-router-dom";
import loginLogo from "../../images/logo-header.svg";

export default function Login(props) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function handleSubmit(e) {
    e.preventDefault();
    props.onLogin(email, password);
    setEmail("");
    setPassword("");
  }

  function handleEmailChange(e) {
    setEmail(e.target.value);
  }

  function handlePasswordChange(e) {
    setPassword(e.target.value);
  }
  return (
    <div className='login'>
      <img className="login__logo" src={loginLogo} alt="Логотип" />
      <p className="login__title">Рады видеть!</p>
      <form className="login-form" onSubmit={handleSubmit}>
        <input
          id="login-email-input"
          type="email"
          name="email"
          className="login__email"
          placeholder="Email:"
          onChange={handleEmailChange}
          value={email}
          required=""
        />
        <span className="login__name-error" />
        <input
          id="login-password-input"
          type="password"
          name="password"
          className="login__password"
          placeholder="Пароль:"
          onChange={handlePasswordChange}
          value={password}
          required=""
        />
        <span className="login__name-error" />
        <input type="submit" value="Зарегистрироваться" className="login__submit" />
        <p className="login__text">Ещё не зарегистрированы? <Link to="/sign-in" className="login__link">Войти</Link></p>
      </form>
    </div>
  )
}
