import React from 'react';
import { useLocation } from 'react-router-dom';
import './MoviesCard.css';
import { CurrentUserContext } from "../../contexts/CurrentUserContext";

export default function MoviesCard({ movie, isSaved }) {
  const location = useLocation();
  const isMainPage = location.pathname === '/movies';
  return (
    <div className='movie'>
      <button type="button" className='movie__save' />
      <img alt={movie.name} className="movie__image" src={movie.image.url} />
      {
        isMainPage ? isSaved ? (
          <div className='movie__save movies-card__button_saved_yes'></div>
        ) :
          (
            <button className='movies-card__button movies-card__button_saved_no' onClick={() => {}}>Сохранить</button>
          ) :
          (
            <button className='movies-card__button movies-card__button_delete'></button>
          )
      }
      <div className="movie__data">
        <h2 className="movie__name">{movie.name}</h2>
        <p className="movie__time">{movie.time}</p>
      </div>
    </div>
  )
}
