import headerLogo from "../../images/logo-header.svg";
import headerLogoProfile from "../../images/profile-header.svg";
import headerBurgerLogo from "../../images/menu-burger.svg";

export default function Header({ loggedIn, isLoading }){
  const { pathname } = useLocation();

  return (
    <header className={`header ${pathname !== '/' ? '' : 'header_type_auth'}`}>
      <Link to="/" className="header__link">
        <img className="header__logo" src={logo} alt="Логотип Movies Explorer"></img>
      </Link>
      {isLoading ? '' : loggedIn ? <Navigation /> : <NavAuth />}
    </header>
  );
};

